import React, { useEffect, useState } from "react";
import { AiOutlineSend } from 'react-icons/ai';
import Bubble from '../Bubble/Bubble';
import './chatStyle.css';

function Chat ({socket, author, room}){

      const [message, setMessage] = useState("");
      const [messageList, setMessageList] = useState([]);
      let id = 0;
      function sendMessage(event) {
            event.preventDefault();
            if(message !== '') {
                  const messageData = {
                        id: ++id,
                        message: message,
                        author: author,
                        room: room,
                        time: new Date(Date.now()).getHours()+" : " + new Date(Date.now()).getMinutes(),
                  }
                  setMessageList((list)=>[...list, messageData]);
                  socket.emit("sendMessage",messageData);
            }

      }
      useEffect(()=>{
            socket.on("recievedMessage",(data)=>{
                  console.log(data);
                  setMessageList((list)=>[...list, data]);
            })
      },[socket])
      return (
            <div className="chat">
                  <div className="chat-header">
                        <h1>Hello <span className="spl">{author}</span></h1>
                        <p>You are now joined Room <span className="spl">{room}</span></p>
                  </div>
                  <div className="chat-body">
                        {/* {
                              messageList.map((messageData) => {
                                    return <p className="chat-bubble" key={messageData.id} id={ messageData.author === author ? "you" : "other" }>{messageData.message}</p>
                              })
                        } */}
                        {
                              messageList.map((messageData) => {
                                    return <Bubble messageData={messageData} author={author} />

                        })}
                  </div>
                  <div className="chat-foot">
                  <input type="text" name="message" placeholder="Type your message" value={message} onChange={
                        (event)=>{
                              setMessage(event.target.value);
                        }
                  }/>
                  <button type="button" onClick={sendMessage}><AiOutlineSend className="send-icon"/></button>
                  </div>
            </div>
      )
}

export default Chat;
