import React from 'react'
import './bubbleStyle.css'

export default function Bubble({messageData, author}) {
    return (
        <div className='bubble-container' id={messageData.author === author? "you" : "other"}>
            <div className="username">{messageData.author === author }</div>
            <div className="bubble">{messageData.message}</div>
            <div className="date">{messageData.time}</div>
        </div>
    )
}
