  }

  return (
    <div className="App">
      <form className="join-form">
        <input type="text"
          value={username}
          onChange={
            (event)=> {
              setUsername(event.target.value);
            }
          }
          placeholder='Your name'
          />
        <input
          type="text"
          name="room"
          value={room}
          onChange={(event)=>{
              setRoom(event.target.value)
            }
          }
          placeholder="Enter room number" />
        <button onClick={joinRoom} type="submit">Join</button>
      </form>

    </div>
  );
}

export default App;
