import React, { useState } from 'react';
import io from 'socket.io-client';
import './App.css';
import Chat from './component/Chat/Chat';
import JoinForm from './component/joinForm/JoinForm';
function App() {
  const socket = io.connect('http://localhost:3001');
  const [room, setRoom] = useState();
  const [username, setUsername] = useState("");
  let showJoinForm = false;

  function setData(data) {
    setRoom(data.room);
    setUsername(data.username);
  }

  function setShowJoinForm(status) {
    showJoinForm = status;
  }

  return (
    <div className="App">
      {!showJoinForm ? <JoinForm socket={socket} setData={setData} setShowJoinForm={setShowJoinForm} /> : 
        <Chat socket={socket} author={username} room={room} />
      }

    </div>
  );
}

export default App;
